function agregarCapacitacion(e,c,d,a,b){if(confirm("¿Desea "+(b?"AGREGAR":"ELIMINAR")+' la capacitación "'+c+'" al instructor "'+a+'"?')){$.post("cat_instructores_capacitaciones_ajax.php",{xAccion:"grabar",xCveCapacitacion:e,xCveInstructor:d,xSeAgrega:b},function(f){if(parseInt(f)!==0){getTablaCapacitaciones(d);alert('Capacitación "'+c+'" agregada con exito al instructor "'+a+'"')}})}else{}}function getTablaCapacitaciones(a){$("#div_resultado").html('<img src="../img/ajax-loading.gif" width="20" height="21" alt="Cargando"/> Cargando ...');$("#div_resultado").load("cat_instructores_capacitaciones_ajax.php",{xAccion:"getTablaCapacitaciones",xCveInstructor:a},function(){$("#tabla-intructores-capacitaciones").DataTable({order:[[0,"desc"]]})})};